func testRepoToManyAddOpCommits(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Repo
	var b, c, d, e Commit

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, repoDBTypes, false, strmangle.SetComplement(repoPrimaryKeyColumns, repoColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*Commit{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, commitDBTypes, false, strmangle.SetComplement(commitPrimaryKeyColumns, commitColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*Commit{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddCommits(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.RepoID {
			t.Error("foreign key was wrong value", a.ID, first.RepoID)
		}
		if a.ID != second.RepoID {
			t.Error("foreign key was wrong value", a.ID, second.RepoID)
		}

		if first.R.Repo != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Repo != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.Commits[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.Commits[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.Commits().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}
