func testReposUpdate(t *testing.T) {
	t.Parallel()

	if 0 == len(repoPrimaryKeyColumns) {
		t.Skip("Skipping table with no primary key columns")
	}
	if len(repoAllColumns) == len(repoPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Repo{}
	if err = randomize.Struct(seed, o, repoDBTypes, true, repoColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Repos().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, repoDBTypes, true, repoPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	if rowsAff, err := o.Update(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("should only affect one row but affected", rowsAff)
	}
}

func testReposSliceUpdateAll(t *testing.T) {
	t.Parallel()

	if len(repoAllColumns) == len(repoPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Repo{}
	if err = randomize.Struct(seed, o, repoDBTypes, true, repoColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Repos().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, repoDBTypes, true, repoPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(repoAllColumns, repoPrimaryKeyColumns) {
		fields = repoAllColumns
	} else {
		fields = strmangle.SetComplement(
			repoAllColumns,
			repoPrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := RepoSlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
}

func testReposUpsert(t *testing.T) {
	t.Parallel()

	if len(repoAllColumns) == len(repoPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	// Attempt the INSERT side of an UPSERT
	o := Repo{}
	if err = randomize.Struct(seed, &o, repoDBTypes, true); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Upsert(ctx, tx, false, nil, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Repo: %s", err)
	}

	count, err := Repos().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}

	// Attempt the UPDATE side of an UPSERT
	if err = randomize.Struct(seed, &o, repoDBTypes, false, repoPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Repo struct: %s", err)
	}

	if err = o.Upsert(ctx, tx, true, nil, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Repo: %s", err)
	}

	count, err = Repos().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}
}
